SELECT a.id, a.file_name, a.file_path, a.file_type, b.team_name as team_id, c.user_name as owner_id, d.tag_name as tag_list, a.modify_time, a.status, a.create_time FROM legal_file_info a LEFT JOIN legal_team_info b ON a.team_id = b.id LEFT JOIN legal_user_info c ON a.owner_id = c.id LEFT JOIN legal_tag_info d ON a.tag_list = d.id
1 = 1 AND a.team_id = #{teamId} AND a.file_type = 1
AND a.status=#{status}
ORDER BY a.id
LIMIT #{start}, #{size}

SELECT a.id, a.file_name, a.file_path, a.file_type, b.team_name as team_id, c.user_name as owner_id,
d.tag_name as tag_list, a.modify_time, a.status, a.create_time
FROM legal_file_info a LEFT JOIN legal_team_info b ON a.team_id = b.id
LEFT JOIN legal_user_info c ON a.owner_id = c.id
LEFT JOIN legal_tag_info d ON a.tag_list = d.id

    1 = 1

        AND a.status=#{status}


        AND a.parent_id=#{parentId}

